-- ----------------------------
-- Table structure for examination
-- ----------------------------
DROP TABLE IF EXISTS `examination`;
CREATE TABLE `examination` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `exam_name` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '试卷名称',
  `Answer_time` int(255) DEFAULT NULL COMMENT '答题时间 分钟',
  `subject_id` int(11) DEFAULT NULL COMMENT '科目类型id',
  `exam_score` float(255,0) DEFAULT NULL COMMENT '试卷总分',
  `passing_score` float(255,0) DEFAULT NULL COMMENT '及格分数',
  `topic_num` int(11) DEFAULT NULL COMMENT '题目数量',
  `create_user_id` int(11) DEFAULT NULL COMMENT '创建者id',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_user_id` int(11) DEFAULT NULL COMMENT '修改人id',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `release_yn` int(10) DEFAULT NULL COMMENT '是否发布 0发布  1未发布',
  `delete_yn` int(255) DEFAULT NULL COMMENT '是否已经删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=DYNAMIC;

