DROP TABLE IF EXISTS `topic`;
CREATE TABLE `topic` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `topic_type` int(10) DEFAULT NULL COMMENT '0 单选题  1 多选题  2判断题 3填空题 4 简答题 5 代码题',
  `question` text COLLATE utf8_bin COMMENT '问题',
