DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(50) COLLATE utf8_bin NOT NULL COMMENT '姓名',
  `nick_name` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '昵称',
  `age` int(3) DEFAULT NULL COMMENT '年龄',
  `sex` int(1) DEFAULT NULL COMMENT '性别',
  `phone` varchar(11) COLLATE utf8_bin NOT NULL COMMENT ' 手机号',
  `password` varchar(255) COLLATE utf8_bin NOT NULL COMMENT '密码',
  `key_uuid` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '密钥',
  `number` int(1) DEFAULT NULL COMMENT '密码错误次数',
  `address` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT '家庭地址',
  `role` int(1) DEFAULT NULL COMMENT '角色',
  `use_yn` int(1) DEFAULT NULL COMMENT '是否正常使用  0 正常使用  1禁用 ',
  `delete_yn` int(1) DEFAULT NULL COMMENT '是否删除  0未删除  1 已删除',
  `registration_time` datetime DEFAULT NULL COMMENT '注册时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `index_phone` (`phone`) USING BTREE COMMENT '唯一索引'
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=DYNAMIC COMMENT='用户表';

